    private void output(String filename, String url) throws Exception {
        System.setProperty("webdriver.chrome.driver", "C:\\Users\\om\\Downloads\\Programs\\Starting Selenium\\chromedriver.exe");
        driver = new ChromeDriver();
        driver.manage().timeouts().implicitlyWait(30, TimeUnit.SECONDS);
        driver.get(url + "/");
        // Take snapshot of visible part.
        get_image(filename);
        // Take snapshot of all remaining part according to scroll size of snapshot by get_image().
        get_different_image(filename);

        driver.quit();
    }

    private void get_image(String file_name) throws Exception {
        File screen = ((TakesScreenshot)this.driver).getScreenshotAs(OutputType.FILE);
        FileUtils.copyFile(screen, new File("C:\\Desktop View\\IE9\\" + file_name + ".png"));
    }

    private void get_different_image(String original_file_name) throws Exception {
        int view_port_height;
        int page_height = driver.findElement(By.className("html")).getSize().getHeight();
        view_port_height = driver.manage().window().getSize().height;
        if (view_port_height >= page_height) {
            return;
        }
        System.out.println(page_height);
        System.out.println(view_port_height);
        int n =  (int) (Math.ceil((double) page_height / view_port_height));
        System.out.println("n= " + n);
        String[] tmp_file = new String[n];
        JavascriptExecutor jse = (JavascriptExecutor)driver;
        String file_name;
        for (int i=0; i<n; i++) {
            view_port_height = driver.manage().window().getSize().height;
            view_port_height = (i == 0) ? view_port_height * -1 : view_port_height - 80;
            jse.executeScript("window.scrollBy(0, " + view_port_height + ")", "");
            Thread.sleep(1000);
            file_name = "temp" + i;
            tmp_file[i] = file_name;
            get_image(file_name);
        }
        MergeImages(tmp_file, original_file_name);
    }

    private void MergeImages(String [ ] args, String original_file_name) throws Exception {
        String path = "C:\\Desktop View\\IE9\\";

        // load source images
        File f1;
        File f2;

        FileUtils.copyFile(new File(path + "combined.png"), new File(path + original_file_name + ".png"));

        for ( int i=0; i<args.length ; i++ ) {

            f1 = new File(path + original_file_name + ".png");

            if (!f1.exists()) {
                f1.createNewFile();
            }

            f1.setReadable(true, true);
            f1.setExecutable(true);
            f1.setWritable(true,true);


            BufferedImage image = ImageIO.read(f1);

            f2 = new File(path + args[i] + ".png");
            f2.setReadable(true, true);
            f2.setExecutable(true);
            f2.setWritable(true,true);

            BufferedImage overlay = ImageIO.read(f2);

            // create the new image, canvas size is the max. of both image sizes
            int w = Math.max(image.getWidth(), overlay.getWidth());
            int h = Math.max(image.getHeight(), overlay.getHeight());
            h = h + Math.min(image.getHeight(), overlay.getHeight());
            BufferedImage combined = new BufferedImage(w, h, BufferedImage.TYPE_INT_ARGB);

            // paint both images, preserving the alpha channels
            Graphics g = combined.getGraphics();
            g.drawImage(image, 0, 0, null);
            g.drawImage(overlay, 0, image.getHeight(), null);

            // Save as new image
            ImageIO.write(combined, "png", new File(path + original_file_name + ".png"));
        }
        deleteTempImage(args);
    }

    private void deleteTempImage(String [ ] args) {
        String path = "C:\\Desktop View\\IE9\\";
        File f2;
        for ( int i=0; i<args.length ; i++ ) {
            f2 = new File(path + args[i] + ".png");
            f2.delete();
        }
    }

